import "core-js/modules/es.array.push.js";
import * as echarts from "echarts";
export default {
  props: {
    chartData: {
      type: Array,
      required: true
    }
  },
  data() {
    return {
      ExpandChart: null
    };
  },
  mounted() {
    this.$nextTick(() => {
      this.initChart(); // 等待 DOM 渲染完成后初始化图表
    });
    window.addEventListener("resize", this.resizeChart); // 窗口大小变化时调整图表尺寸
  },
  beforeDestroy() {
    window.removeEventListener("resize", this.resizeChart); // 防止内存泄漏
  },
  watch: {
    chartData: {
      immediate: true,
      handler(newValue) {
        this.updateChart(newValue);
      }
    }
  },
  methods: {
    resizeChart() {
      if (this.ExpandChart) {
        this.ExpandChart.resize(); // 调整图表尺寸
      }
    },
    initChart() {
      const chartDom = this.$refs.ExpandChart;
      this.ExpandChart = echarts.init(chartDom);

      // 构造 series 数据
      const series = this.chartData.slice(1).map(row => ({
        name: row[0],
        // 系列名称，使用每行的第一个元素作为名称
        type: "line",
        data: row.slice(1),
        // 数据，使用每行的剩余元素作为数据
        smooth: true,
        seriesLayoutBy: "row",
        emphasis: {
          focus: "series"
        }
      }));

      // 添加 pie chart 配置
      series.push({
        type: "pie",
        id: "pie",
        radius: "30%",
        center: ["50%", "25%"],
        // 调整饼图的位置
        emphasis: {
          focus: "self"
        },
        label: {
          formatter: "{b}: {@currentMonth} ({d}%)"
        },
        encode: {
          itemName: "product",
          value: "currentMonth",
          tooltip: "currentMonth"
        },
        z: 10 // 提高饼图的层级，避免被折线图遮挡
      });
      const option = {
        legend: {},
        tooltip: {
          trigger: "axis",
          showContent: false
        },
        dataset: {
          source: this.chartData
        },
        xAxis: {
          type: "category",
          data: this.chartData[0].slice(1) // 使用第一行的数据作为x轴
        },
        yAxis: {
          type: 'value' // 设置y轴类型为值轴，避免出现找不到yAxis的错误
        },
        grid: {
          top: "55%" // 适当的调整 grid 的位置
        },
        series
      };
      this.ExpandChart.setOption(option);
    },
    updateChart(newValue) {
      if (this.ExpandChart) {
        const series = newValue.slice(1).map(row => ({
          name: row[0],
          // 系列名称，使用每行的第一个元素作为名称
          type: "line",
          data: row.slice(1),
          // 数据，使用每行的剩余元素作为数据
          smooth: true,
          seriesLayoutBy: "row",
          emphasis: {
            focus: "series"
          }
        }));
        series.push({
          type: "pie",
          id: "pie",
          radius: "30%",
          center: ["50%", "25%"],
          // 调整饼图的位置
          emphasis: {
            focus: "self"
          },
          label: {
            formatter: "{b}: {@currentMonth} ({d}%)"
          },
          encode: {
            itemName: "product",
            value: "currentMonth",
            tooltip: "currentMonth"
          },
          z: 10 // 提高饼图的层级，避免被折线图遮挡
        });
        this.ExpandChart.setOption({
          dataset: {
            source: newValue
          },
          xAxis: {
            data: newValue[0].slice(1) // 更新x轴数据
          },
          yAxis: {
            type: 'value' // 保证y轴类型是value
          },
          series
        });
      }
    }
  }
};
