import * as echarts from "echarts";
export default {
  props: {
    chartData: {
      type: Array,
      required: true
    }
  },
  data() {
    return {
      chart: null
    };
  },
  mounted() {
    this.initChart();
  },
  methods: {
    initChart() {
      const chartDom = this.$refs.chart;
      this.chart = echarts.init(chartDom);
      const option = {
        legend: {},
        tooltip: {
          trigger: "axis",
          showContent: false
        },
        dataset: {
          source: this.chartData
        },
        xAxis: {
          type: "category"
        },
        yAxis: {
          gridIndex: 0
        },
        grid: {
          top: "55%"
        },
        series: [{
          type: "line",
          smooth: true,
          seriesLayoutBy: "row",
          emphasis: {
            focus: "series"
          }
        }, {
          type: "line",
          smooth: true,
          seriesLayoutBy: "row",
          emphasis: {
            focus: "series"
          }
        }, {
          type: "line",
          smooth: true,
          seriesLayoutBy: "row",
          emphasis: {
            focus: "series"
          }
        }, {
          type: "line",
          smooth: true,
          seriesLayoutBy: "row",
          emphasis: {
            focus: "series"
          }
        }, {
          type: "pie",
          id: "pie",
          radius: "30%",
          center: ["50%", "25%"],
          emphasis: {
            focus: "self"
          },
          label: {
            formatter: "{b}: {@currentMonth} ({d}%)"
          },
          encode: {
            itemName: "product",
            value: "currentMonth",
            tooltip: "currentMonth"
          }
        }]
      };
      this.chart.setOption(option);
      this.chart.on("updateAxisPointer", event => {
        const xAxisInfo = event.axesInfo[0];
        if (xAxisInfo) {
          const dimension = xAxisInfo.value + 1;
          this.chart.setOption({
            series: {
              id: "pie",
              label: {
                formatter: "{b}: {@[" + dimension + "]} ({d}%)"
              },
              encode: {
                value: dimension,
                tooltip: dimension
              }
            }
          });
        }
      });
    }
  }
};
